export const UserDataAction = (defaultAddress, tronweb) => async (dispatch) => {
  // console.log("called", auth);
  if (defaultAddress) {
    tronweb
      .contract()
      .at(environment.contractAddress)
      .then(async (contract) => {
        //user wallet address
        dispatch({
          type: "USER_WALLET_ADDRESS",
          payload: defaultAddress,
        });

        let DownLinde = [];
        contract
          .getdirects(defaultAddress)
          .call()
          .then(async (val) => {
            // console.log("this is  getdirects");
            dispatch({
              type: "DOWNLINE_COUNT",
              payload: val.arr,
            });

            //binary tree
            await contract
              .users(defaultAddress)
              .call()
              .then((val) => {
                binaryTree.name = val.registrationid.toNumber();
              });

            if (val.arr.length) {
              for (let a = 1; a >= 0; a--) {
                // console.log("=====>",a);
                let cond = a === 1 ? 0 : 1;
                // console.log(cond);
                if (val.arr[cond]) {
                  await contract
                    .users(tronweb.address.fromHex(val.arr[cond]))
                    .call()
                    .then((user) => {
                      binaryTree.children[
                        a
                      ].name = user.registrationid.toNumber();
                    });
                }

                if (val.arr.length) {
                  await contract
                    .getdirects(tronweb.address.fromHex(val.arr[1]))
                    .call()
                    .then(async (value) => {
                      if (value.arr[a]) {
                        await contract
                          .users(tronweb.address.fromHex(value.arr[a]))
                          .call()
                          .then((user) => {
                            binaryTree.children[0].children[
                              cond
                            ].name = user.registrationid.toNumber();
                          });
                      }
                    });

                  if (val.arr[0]) {
                    await contract
                      .getdirects(tronweb.address.fromHex(val.arr[0]))
                      .call()
                      .then(async (value) => {
                        if (value.arr[a]) {
                          await contract
                            .users(tronweb.address.fromHex(value.arr[a]))
                            .call()
                            .then((user) => {
                              binaryTree.children[1].children[
                                cond
                              ].name = user.registrationid.toNumber();
                            });
                        }
                      });
                  }
                }
              }
            }

            dispatch({
              type: "BINARY_TREE",
              payload: binaryTree,
            });

            // console.log("=====>",binaryTree);
          });

        contract
          .users(defaultAddress)
          .call()
          .then((val) => {
            dispatch({
              type: "WITHDRAW_ABLE",
              payload: tronweb.fromSun(val.withdrawable),
            });

            dispatch({
              type: "USERS_ID",
              payload: val.registrationid.toNumber(),
            });

            dispatch({
              type: "USERS_ID_FOR_INCOME",
              payload: val.registrationid.toNumber(),
            });

            dispatch({
              type: "LEVEL_REWARD",
              payload: tronweb.fromSun(val.LevelReward),
            });
            dispatch({
              type: "MATRIX_INCOME",
              payload: tronweb.fromSun(val.MatrixReward),
            });
            dispatch({
              type: "DIRECT_REFERRALS",
              payload: val.referredUsers.toNumber(),
            });
            dispatch({
              type: "LEVEL_INCOME",
              payload: tronweb.fromSun(val.LevelReward),
            });
          });

        //contract
        dispatch({
          type: "CONTRACT",
          payload: contract,
        });

        //tronweb
        dispatch({
          type: "TRONWEB",
          payload: tronweb,
        });

        // business stations
        for (let i = 1; i <= 7; i++) {
          await contract
            .getStationInfo(defaultAddress, i)
            .call()
            .then((val) => {
              dispatch({
                type: `STATION_${i}`,
                payload: val[0],
              });
            });
        }

        //smart matrix

        //smart level 1
        await contract
          .getStationInfo(defaultAddress, 8)
          .call()
          .then((val) => {
            dispatch({
              type: "SMART_MATRIX_LEVEL_ONE",
              payload: val[0],
            });
          });

        //smart level 2
        await contract
          .getStationInfo(defaultAddress, 9)
          .call()
          .then((val) => {
            dispatch({
              type: "SMART_MATRIX_LEVEL_TWO",
              payload: val[0],
            });
          });

        //silver matrix

        //sliver level 1
        await contract
          .getStationInfo(defaultAddress, 10)
          .call()
          .then((val) => {
            dispatch({
              type: "SILVER_MATRIX_LEVEL_ONE",
              payload: val[0],
            });
          });

        //silver level 2
        await contract
          .getStationInfo(defaultAddress, 11)
          .call()
          .then((val) => {
            dispatch({
              type: "SILVER_MATRIX_LEVEL_TWO",
              payload: val[0],
            });
          });

        // gold matrix

        //gold level 1
        await contract
          .getStationInfo(defaultAddress, 12)
          .call()
          .then((val) => {
            dispatch({
              type: "GOLD_MATRIX_LEVEL_ONE",
              payload: val[0],
            });
          });

        //gold level 2
        await contract
          .getStationInfo(defaultAddress, 13)
          .call()
          .then((val) => {
            dispatch({
              type: "GOLD_MATRIX_LEVEL_TWO",
              payload: val[0],
            });
          });

        // Royal matrix

        //Royal level 1
        await contract
          .getStationInfo(defaultAddress, 14)
          .call()
          .then((val) => {
            dispatch({
              type: "ROYAL_MATRIX_LEVEL_ONE",
              payload: val[0],
