import React, { useState } from "react";
import { Progress } from "react-sweet-progress";
import "react-sweet-progress/lib/style.css";
import { useSelector, useDispatch } from "react-redux";
import {
  smartMatrixAction,
  smartMatrixLevel2Action,
  silverMatrixAction,
  silverMatrixLevel2Action,
  goldMatrixAction,
  goldMatrixLevel2Action,
  royalMatrixAction,
  royalMatrixLevel2Action,
  crownMatrixAction,
  crownMatrixLevel2Action,
} from "../../../redux/actions";
import CircleLoader from "react-spinners/CircleLoader";
import { css } from "@emotion/core";
import impButn from "./buyButn.png";
import impButninActive from "./inActive.png";
import { toast } from "react-toastify";
/**
 * @author
 * @function Home
 **/

const Home = () => {
  <Progress percent={88} status="success" />;
  const dispatch = useDispatch();

  const {
    bStation1,
    bStation2,
    bStation3,
    bStation4,
    bStation5,
    bStation6,
    bStation7,
    smartMLevelOne,
    smartMLevelTwo,
    silverMLevelOne,
    silverMLevelTwo,
    goldMLevelOne,
    goldMLevelTwo,
    royalMLevelOne,
